import axios from "axios";

export const getAllEmployees = () => {
  //return axios.get("/api/user/all");
  //alert( axios.defaults.headers.common["Authorization"]);
  return axios.get("http://localhost:8092/sample/api/employee?page=0&size=1");  
};
//http://localhost:8081/shop/api/employees
export const getUserById = id => {
  return axios.get("/api/user", { data: id });
};
export const deleteUser = User => {
  return axios.post("/api/user/delete", User);

};
export const addNewUser = User => {
  //"/api/user/add"
  return axios.post("http://localhost:8092/sample/api/employee/add", User);
};
export const updateUser = User => {
  return axios.post(`http://localhost:8092/sample/api/employee/update`, User);
};
